comp_hlsl:
shader_body
{
//initial textures:
//1. Main image
     float3 base=tex2D(sampler_main, uv);
//2. High quality noise sampler
     float3 hinoise=tex2D(sampler_noise_hq, uv);

//declaring modifying vars:
//1. Perspective UV value
     float2 uvp=uv;
     uvp.y*=.38;
//2. Blur values
     float3 blpi=GetBlur1(uvp);
     float3 blpii=GetBlur2(uvp);
     float3 blpiii= GetBlur3(uvp);
     float3 blmi=GetBlur1(uv);
     float3 blmii=GetBlur2(uv);
     float3 blmiii=GetBlur3(uv);

//Perspective modified samplers
//1. Perspective Main
     float3 pret=tex2D(sampler_main, uvp);

//final return value     
float3 fret=(1/blpii-(pret*blpi)).xyz;
fret-=blmii*0.55;

ret=1-fret*fret;  
}
