comp:
shader_body { 
 vec3 fret_1;
 vec2 uvp_2;
 uvp_2.x = uv.x;
 uvp_2.y = (uv.y * 0.38);
 fret_1 = (((1.0/(
   ((texture (sampler_blur2, uvp_2).xyz * scale2) + bias2)
 )) - (
   (texture (sampler_main, uvp_2).xyz - (texture (sampler_noisevol_hq, ((
     (vec3(0.05, 0.05, 0.0) * uv.xyy)
    * 
     ((uvp_2.yxx * texsize.xyy) * texsize_noisevol_hq.zww)
   ) + (vec3(0.0, 0.0, 0.4) * time))) * 0.5).xyz)
  * 
   ((texture (sampler_blur1, uvp_2).xyz * scale1) + bias1)
 )) - ((
   (texture (sampler_blur2, uv).xyz * scale2)
  + bias2) * 0.55));
 vec4 tmpvar_3;
 tmpvar_3.w = 1.0;
 tmpvar_3.xyz = (1.0 - (fret_1 * fret_1));
