waves.point_eqs_eel:
x = .5 + (bass*.2)*sin(sample*2*(time*10*treb));
y = .5 + (bass*.2)*cos(sample*2*(time*10*treb));
r = 1 + .5*sin(sample*.10+(time*10*bass));
g = 1 + .5*sin(sample*2+(time*50*treb));
b = 1 + .5*sin(sample*5+(time*20*mid));
a = r;

frame_eqs_eel:
warp = 0;
bassspin = if(above(bass*.09,bassspin),bassspin+.002,bassspin-.002);
bassspin = bassspin*above(bassspin,0);
bassc = bassc+bassspin;
q1 = bassc;
q2 = frame%int(36+12*sin(time*.1));
tst = if(equal(q2,1),10+int(rand(45)),tst);
q3 = tst;
decay = .92;

pixel_eqs_eel:
zoom = zoom + (.03*(bass_att*bass_att))*rad;
rot = rot + rad*(-2.5&(cos(time)*5))*.01;
zoom = zoom+((treb_att*.01));

warp:
shader_body { 
 vec4 tmpvar_1;
 tmpvar_1.w = 1.0;
 tmpvar_1.xyz = (texture (sampler_fw_main, uv).xyz - 0.04);
 ret = tmpvar_1.xyz;
}

comp:
shader_body { 
 vec3 ret_1;
 ret_1 = (texture (sampler_main, uv).xyz + ((texture (sampler_blur2, uv).xyz * scale2) + bias2));
 ret_1 = (ret_1 + ((0.8 * 
   (hue_shader - 0.8)
 ) * (1.0 - uv.y)));
 vec4 tmpvar_2;
 tmpvar_2.w = 1.0;
 tmpvar_2.xyz = ret_1;
 ret = tmpvar_2.xyz;
}

warp_hlsl:
shader_body
{
    // sample previous frame
    ret = tex2D( sampler_fw_main, uv ).xyz;
    // darken (decay) over time
    ret = ret-.04;
}
