frame_eqs_eel:
wave_r = 0.85 + 0.25*sin(0.437*time+1);
wave_g = 0.85 + 0.25*sin(0.544*time+2);
wave_b = 0.85 + 0.25*sin(0.751*time+3);
dx = dx + 0.003*( 0.60*sin(0.234*time) + 0.40*sin(0.277*time) );
dy = dy + 0.003*( 0.60*sin(0.284*time) + 0.40*sin(0.247*time) );
decay = decay - 0.01*equal(frame%6,0);
dx = dx + dx_residual;
dy = dy + dy_residual;
bass_thresh = above(bass_att,bass_thresh)*2 + (1-above(bass_att,bass_thresh))*((bass_thresh-1.3)*0.96+1.3);
dx_residual = equal(bass_thresh,2.13)*0.016*sin(time*7) + (1-equal(bass_thresh,2.13))*dx_residual;
dy_residual = equal(bass_thresh,2.13)*0.012*sin(time*9) + (1-equal(bass_thresh,2.13))*dy_residual;
wave_x = wave_x - dx_residual*7;
wave_y = wave_y - dy_residual*7;
//wave_mystery = time*0.03;

new_rot = rot + 0.013*( 0.60*sin(17.381*time) + 0.40*sin(11.579*time) );
new_zoom = zoom + 0.01*( 0.60*sin(20.1934*time+3) + 0.40*sin(16.307*time+9) );
new_warp = warp + 0.34*( 0.60*sin(13.5442*time) + 0.40*sin(22.543*time) );
new_cx = cx + 0.310*( 0.60*sin(16.374*time) + 0.40*sin(26.294*time) );
new_cy = cy + 0.310*( 0.60*sin(18.393*time) + 0.40*sin(37.223*time) );

// this is a great way to respond to beats:
// once you get one, let it decay at a constant rate!!
rg = max(rg*0.77, 0.02 + 0.5*min(2,max(0,mid_att-1)*1.3));
q9 = rg;



// custom wave opacity modulation
//beat = (bass+mid+treb) / (bass_att+mid_att+treb_att);
beat = max(bass/bass_att, mid/mid_att);
beat = max(beat, treb/treb_att);
 decay_rate = pow(0.9995, fps);
 beat = max( beat, prev_beat*decay_rate );
  wave_a = (beat - prev_beat - 0.05)*24;
  is_beat = above(wave_a, 0.5);
  rot = (1-is_beat)*my_rot + (is_beat)*new_rot;
  zoom = (1-is_beat)*my_zoom + (is_beat)*new_zoom;
  warp = (1-is_beat)*my_warp + (is_beat)*new_warp;
  cx = (1-is_beat)*my_cx + (is_beat)*new_cx;
  cy = (1-is_beat)*my_cy + (is_beat)*new_cy;
prev_beat = beat;
my_rot = rot;
my_zoom = zoom;
my_warp = warp;
my_cx = cx;
my_cy = cy;

