warp_hlsl:
shader_body
{
    // sample previous frame
    ret = tex2D( sampler_main, uv ).xyz;

    // take the difference between the crisp and blurred images,
    // then add it back into the image.  Creates spots and stripes over time.
    ret += (ret - GetBlur1(uv))*0.3;
    ret *= 0.9;

   // add noise:
   float2 dither_uv = uv_orig*texsize.xy*texsize_noise_lq.zw * 0.1;// + rand_frame.xy;
   ret += (tex2D(sampler_noise_lq, dither_uv).xyz-0.5)/256.0 * 28;

   // desaturate over time, to keep the globs white
   ret = lerp(ret, lum(ret), 0.04);
}
