comp:
shader_body { 
 vec2 uv_1;
 vec3 ret2_2;
 vec2 uv2_3;
 vec3 ret_4;
 uv2_3.y = uv.y;
 uv2_3.x = (1.0 - uv.x);
 uv_1 = (0.05 + (0.9 * uv));
 uv2_3 = (0.05 + (0.9 * uv2_3));
 ret_4 = (texture (sampler_main, uv_1).xyz * 4.0);
 ret2_2 = (texture (sampler_main, uv2_3).xyz * 4.0);
 ret_4 = (ret_4 - ((
   (texture (sampler_blur1, uv_1).xyz * scale1)
  + bias1) * 4.0));
 ret2_2 = (ret2_2 - ((
   (texture (sampler_blur1, uv2_3).xyz * scale1)
  + bias1) * 4.0));
 ret_4 = -(ret_4);
 ret2_2 = -(ret2_2);
 ret_4 = ((pow (ret_4, vec3(0.5, 0.5, 0.7)) - 0.1) * 1.1);
 ret2_2 = ((pow (ret2_2, vec3(0.7, 0.3, 0.5)) - 0.1) * 1.1);
 ret_4 = (ret_4 + ret2_2);
 vec4 tmpvar_5;
 tmpvar_5.w = 1.0;
 tmpvar_5.xyz = ret_4;
 ret = tmpvar_5.xyz;
}

warp_hlsl:
shader_body
{
    // sample previous frame
    ret = tex2D( sampler_main, uv ).xyz;

    // feather pen
    ret = max(ret, tex2D( sampler_main, (uv-0.5)*(1-8*length(texsize.zw)) + 0.5 ).xyz);
    ret = max(ret, tex2D( sampler_main, (uv-0.5)*(1+8*length(texsize.zw)) + 0.5 ).xyz);

    // darken over time
    ret -= 0.034;
}

comp_hlsl:
