}

comp:
shader_body { 
 vec3 ret_1;
 vec4 tmpvar_2;
 tmpvar_2 = texture (sampler_main, uv);
 float tmpvar_3;
 tmpvar_3 = ((texture (sampler_blur2, uv).xyz * scale2) + bias2).x;
 ret_1 = (tmpvar_2.xyz * 2.4);
 ret_1 = (ret_1 * texture (sampler_main, (tmpvar_3 + (tmpvar_3 * tmpvar_2.xyz).xy)).xyz);
 vec4 tmpvar_4;
 tmpvar_4.w = 1.0;
