}

warp_hlsl:
shader_body
{
    // sample previous frame
    ret = tex2D( sampler_main, uv ).xyz;

    // darken (decay) over time
    ret *= .98; //or try: ret -= 0.004;
}

comp_hlsl:
shader_body
{

    ret = tex2D(sampler_main,uv).xyz;

    float bl = GetBlur2(uv);
    float2 tex = bl*ret.xyz;

    ret *= 2.4;
    ret *= tex2D(sampler_main, bl+tex);
}
