warp_hlsl:
shader_body
{


    float3 rc = GetPixel(uv)+GetBlur1(uv);//+GetPixel(uv);
    uv = uv-float2(0,1);
    float2 ruv = 0.5 + (uv-0.5)*(1+(rc.y*0.03));  

    ruv = frac(float2(ruv.x+pow(rc.x,0), ruv.y+pow(rc.x, 0.005)));

    float3 noise3 = tex2D(sampler_noise_lq, uv_orig*texsize.xy*texsize_noise_lq.zw+rand_frame.xy);
    noise3 *= frac(q15);
    noise3 = lerp(noise3, float3(0,0,0), frac(q3*0.5));

    ret = tex2D(sampler_main, float2(ruv.x, ruv.y)).xyz;

    float3 noise2 = lerp(noise3,float3(q10,q11,q12), ret.x);    

    ret += saturate(cross((ret), (noise2)));
    ret *= 1; 


}

comp_hlsl:
shader_body
{
    float2 uv2, uv3;
    float ang2, c, s;

    uv -= 0.5;
    uv *= aspect.xy;    

    ang2 = 6.28*0.333;
    c = cos(ang2);
    s = sin(ang2);
    uv2.x =  uv.x*c - uv.y*s;
    uv2.y =  uv.x*s + uv.y*c;

    ang2 = 6.28*0.667;
    c = cos(ang2);
    s = sin(ang2);
    uv3.x =  uv.x*c - uv.y*s;
    uv3.y =  uv.x*s + uv.y*c;

    ret = tex2D(sampler_main, uv + 0.5).xyz;
    ret = max(ret, tex2D(sampler_main,uv2 + 0.5).xyz);
    ret = max(ret, tex2D(sampler_main,uv3 + 0.5).xyz);

    ret *= 1.3;
    ret *= hue_shader*4-2;

}
