frame_eqs_eel:
//krash's beat detection code
volume = 0.3*(bass+mid+att);
beatrate = equal(beatrate,0) + (1-equal(beatrate,0))*(below(volume,0.01) + (1-below(volume,0.01))*beatrate);
lastbeat = lastbeat + equal(lastbeat,0)*time;
meanbass_att = 0.1*(meanbass_att*9 + bass_att);
peakbass_att = max(bass_att,peakbass_att);
beatrate = max(if(beat,if(below(time-lastbeat,2*beatrate),0.1*(beatrate*9 + time - lastbeat),beatrate),beatrate),0.1);
peakbass_att = beat*bass_att + (1-beat)*peakbass_att*(above(time - lastbeat, 2*beatrate)*0.95 + (1-above(time - lastbeat, 2*beatrate))*0.995);
lastbeat = beat*time + (1-beat)*lastbeat;
peakbass_att = max(peakbass_att,1.1*meanbass_att);
beat = above(volume,0.8)*below(peakbass_att - bass_att, 0.05*peakbass_att)*above(time - lastbeat, 0.1 + 0.5*(beatrate - 0.1));
beatcounter = beatcounter + beat;
mode = if(beat*equal(beatcounter%2,0),1-mode,mode);
flip = 2*mode-1;
monitor=flip;
q8=flip;


decay=0.98;
zoom=1.002;

vol=(bass_att+mid_att+treb_att)*0.25;
vol=vol*vol;
mtime=mtime+vol*0.1*flip;

q1=mtime*0.4;

warp=0.0

warp:
shader_body { 
 vec4 tmpvar_1;
 tmpvar_1.w = 1.0;
 tmpvar_1.xyz = (texture (sampler_main, (uv + (
   (cos(((texture (sampler_main, uv).xy * 27.9) + (time * vec2(0.13, 0.25)))) * texsize.zw)
  * 0.3))).xyz - 0.005);
 ret = tmpvar_1.xyz;
}

warp_hlsl:
shader_body
{  
    float2 uv2 = uv;

    // PAINTERLY EFFECT - base high-frequency motion on image colors
    float mipbias = 0;//3 + 2*cos(_frame * 2);
    float2 delta2 = 0;//texsize.zw*float2(1,1);
    float3 blurry_color = tex2D( sampler_main, uv2 + delta2 ).xyz;
    uv2.xy += cos(blurry_color.xy*27.9 + time*float2(0.13,0.25)) * texsize.zw*0.3;

    // sample previous frame
    ret = tex2D( sampler_main, uv2 ).xyz;

    // darken over time
    ret -= 0.005; 
}
