warp:
shader_body { 
 vec3 ret_1;
 vec2 tmpvar_2;
 tmpvar_2 = (vec2(1.5, 0.0) * texsize.wz);
 vec2 tmpvar_3;
 tmpvar_3 = (vec2(0.0, 1.5) * texsize.wz);
 ret_1 = (texture (sampler_fc_main, uv).xyz + max (max (texture (sampler_main, 
   (uv + tmpvar_2)
 ).xyz, texture (sampler_main, 
   (uv - tmpvar_2)
 ).xyz), max (texture (sampler_main, 
   (uv + tmpvar_3)
 ).xyz, texture (sampler_main, 
   (uv - tmpvar_3)
 ).xyz)));
 ret_1 = (ret_1 - texture (sampler_main, uv).xyz);
 ret_1 = ((ret_1 * 0.94) - 0.003);
 vec4 tmpvar_4;
 tmpvar_4.w = 1.0;
 tmpvar_4.xyz = ret_1;
 ret = tmpvar_4.xyz;
}

comp:
shader_body { 
 vec3 ret_1;
 vec2 tmpvar_2;
 tmpvar_2 = ((0.5 - uv) + 0.5);
 ret_1 = (abs(mix (
   ((1.5 * texture (sampler_main, uv).xyz) - (1.25 * ((texture (sampler_blur2, uv).xyz * scale2) + bias2)))
 , 
   ((1.5 * texture (sampler_main, tmpvar_2).xyz) - (1.25 * ((texture (sampler_blur2, tmpvar_2).xyz * scale2) + bias2)))
 , vec3(0.5, 0.5, 0.5))) * 1.33);
 ret_1 = (ret_1 * (0.25 + (0.75 * ret_1)));
 vec4 tmpvar_3;
 tmpvar_3.w = 1.0;
 tmpvar_3.xyz = ret_1;
 ret = tmpvar_3.xyz;
}

warp_hlsl:
shader_body
{
    // sample previous frame
    ret = tex2D( sampler_fc_main, uv ).xyz;


ret += max(
max(GetPixel(uv+float2(1.5,0)*texsize.wz),GetPixel(uv-float2(1.5,0)*texsize.wz)),
max(GetPixel(uv+float2(0,1.5)*texsize.wz),GetPixel(uv-float2(0,1.5)*texsize.wz)));
ret -= GetPixel(uv);
    // darken (decay) over time
ret = ret*.94-.003; //or try: ret -= 0.004;
}

comp_hlsl:
shader_body
{
    float2 uv_echo = (uv - 0.5)*float2(-1,-1) + 0.5;
    ret = abs(lerp( 1.5*tex2D(sampler_main, uv).xyz-1.25*GetBlur2(uv), 
                1.5*tex2D(sampler_main, uv_echo).xyz-1.25*GetBlur2(uv_echo), 
                0.50 
              )); //video echo
    ret *= 1.33; //gamma
    ret *= .25+.75*ret;
    //ret *= hue_shader; //old hue shader effect
}
