comp:
shader_body { 
 vec2 uv_1;
 vec3 ret_2;
 uv_1 = (0.05 + (0.9 * uv));
 vec2 tmpvar_3;
 tmpvar_3 = (((uv_1 - 0.5) * vec2(-1.0, 1.0)) + 0.5);
 ret_2 = (abs((
   ((texture (sampler_blur1, uv_1).xyz * scale1) + bias1)
  - 
   mix (texture (sampler_main, uv_1).xyz, texture (sampler_main, tmpvar_3).xyz, vec3(0.5, 0.5, 0.5))
 )) * 6.0);
 ret_2 = (ret_2 * (0.1 + abs(
   max (sin(bass_att), sin(treb_att))
 )));
 ret_2 = (ret_2 * max ((
   (texture (sampler_blur3, uv_1).xyz * scale3)
  + bias3), (
   (texture (sampler_blur3, tmpvar_3).xyz * scale3)
  + bias3)));
 ret_2 = (ret_2 * 1.5);
 vec4 tmpvar_4;
