shader_body
{
    uv = 0.05 + 0.9*uv;


    float2 uv_echo = (uv - 0.5)*float2(-1,1) + 0.5;
    ret = lerp( tex2D(sampler_main, uv).xyz, 
                tex2D(sampler_main, uv_echo).xyz, 
                0.50 
              ); //video echo
    //ret = tex2D(sampler_main, uv).xyz;
      // SUPER GLOW EDGES - looks awesome w/octopus
