mv_x=64;mv_y=48;
nut=0;
stp=0;stq=0;
rtp=0;rtq=0;
wvr=0;
decay=0;
dcsp=0;
q1=0;q2=0;q3=0

frame_eqs_eel:
decay=0.96;

speed=0.900;
speedinv=1-speed;
q1=(qa*speed + bass*speedinv);
q2=(qb*speed + mid *speedinv);
q3=(qc*speed + treb*speedinv);
qa=q1;
qb=q2;
qc=q3;

pixel_eqs_eel:
rot=sin(rad*14 +time)*0.001;
rot=rot + sin(ang*8 +time)*0.001;
dx=sin(rad*48 + time)*0.002;
dy=cos(ang*48 +time)*0.002;
zoom=1 + sin(x*44 + time)*0.034 + cos(y*44 + time)*0.034;

warp:
shader_body { 
 vec3 ret_1;
 ret_1 = (texture (sampler_main, uv).xyz - (roam_sin.wzy * roam_cos.zxy));
 ret_1 = (ret_1 - (ret_1 * 0.5));
 ret_1 = ((ret_1 * abs(
   (((texture (sampler_blur2, uv).xyz * scale2) + bias2) - 0.5)
 )) * 5.0);
 ret_1 = (ret_1 - 0.01);
 vec4 tmpvar_2;
 tmpvar_2.w = 1.0;
 tmpvar_2.xyz = ret_1;
 ret = tmpvar_2.xyz;
}

comp:
shader_body { 
 vec3 ret_1;
 vec3 tmpvar_2;
 tmpvar_2.xy = uv;
 tmpvar_2.z = q2;
 vec2 tmpvar_3;
 vec2 tmpvar_4;
 tmpvar_4 = (uv - 0.5);
 tmpvar_3 = ((tmpvar_4 * vec2(-1.0, 1.0)) + 0.5);
 ret_1 = (((
   (mix (texture (sampler_main, ((tmpvar_4 * 0.98) + 0.5)).xyz, texture (sampler_main, ((
     (tmpvar_3 - 0.5)
    * 0.98) + 0.5)).xyz, vec3(0.5, 0.5, 0.5)) + ((texture (sampler_blur3, uv).xyz * scale3) + bias3))
  + 
   ((texture (sampler_blur3, tmpvar_3).xyz * scale3) + bias3)
 ) + (
   (texture (sampler_blur1, uv).xyz * scale1)
  + bias1)) + ((texture (sampler_blur1, tmpvar_3).xyz * scale1) + bias1));
 ret_1 = (ret_1 * texture (sampler_noisevol_hq, (tmpvar_2 - (roam_sin.zyx * roam_cos.ywx))).xyz);
 ret_1 = (ret_1 * 2.0);
 vec4 tmpvar_5;
 tmpvar_5.w = 1.0;
 tmpvar_5.xyz = ret_1;
 ret = tmpvar_5.xyz;
}

warp_hlsl:
shader_body
{
    // sample previous frame
    ret = tex2D( sampler_main, uv ).xyz;

    // darken (decay) over time
ret-=roam_sin.wzy*roam_cos.zxy;
    ret = ret- (ret*.5);
    ret = ret*(abs(GetBlur2(uv)-.5))*5;
    ret = ret - .01;
}

comp_hlsl:
shader_body
{
    float3 txr = float3(uv,q2);
txr-=roam_sin.zyx*roam_cos.ywx;
    float2 uv_echo = (uv - 0.5)*float2(-1,1) + 0.5;
    ret = lerp( tex2D(sampler_main, (uv-0.5)*0.98 + 0.5).xyz, 
                tex2D(sampler_main, (uv_echo-0.5)*0.98 + 0.5).xyz, 
                0.50 
              )  + GetBlur3(uv) + GetBlur3(uv_echo)
              + GetBlur1(uv) + GetBlur1(uv_echo); //video echo; //video echo
    ret *= tex3D(sampler_noisevol_hq, txr); 
   ret *= 2.0; //gamma
    //ret =  ret*(1 - ret)*10; //brighten
}
