warp:
shader_body { 
 vec4 tmpvar_1;
 tmpvar_1.w = 1.0;
 float tmpvar_2;
 tmpvar_2 = dot (texsize.zw, texsize.zw);
 vec2 tmpvar_3;
 tmpvar_3 = (uv - 0.5);
 tmpvar_1.xyz = (max (max (texture (sampler_main, uv).xyz, texture (sampler_main, 
   ((tmpvar_3 * (1.0 - (8.0 * 
     sqrt(tmpvar_2)
   ))) + 0.5)
 ).xyz), texture (sampler_main, (
   (tmpvar_3 * (1.0 + (8.0 * sqrt(tmpvar_2))))
  + 0.5)).xyz) - 0.034);
 ret = tmpvar_1.xyz;
}

comp:
shader_body { 
 vec3 ret_1;
 vec4 tmpvar_2;
 tmpvar_2 = texture (sampler_main, uv);
 vec2 tmpvar_3;
 tmpvar_3.x = uv.y;
 tmpvar_3.y = abs((uv.x - 1.0));
 vec4 tmpvar_4;
 tmpvar_4 = texture (sampler_main, tmpvar_3);
 ret_1 = (tmpvar_2.xyz + ((tmpvar_2.xyz * tmpvar_2.xyz) * 10.0));
 vec3 tmpvar_5;
 tmpvar_5 = mix ((tmpvar_4.xyz + (
   (tmpvar_4.xyz * tmpvar_4.xyz)
  * 10.0)), ret_1, vec3(0.5, 0.5, 0.5));
 vec3 tmpvar_6;
 tmpvar_6 = mix (tmpvar_5, (tmpvar_2.xyz + (
   (tmpvar_2.xyz * tmpvar_2.xyz)
  * 
   (5.0 * treb)
 )), (tmpvar_5 * bass));
 ret_1 = tmpvar_6;
 vec4 tmpvar_7;
 tmpvar_7.w = 1.0;
 tmpvar_7.xyz = tmpvar_6;
 ret = tmpvar_7.xyz;
}

