warp_hlsl:
shader_body
{
  // sample previous frame
  ret = tex2D( sampler_pw_main, uv ).xyz;

  float n;
  float2 d = texsize.zw*1;
  n  = saturate(tex2D(sampler_pw_main, uv+d*float2( 1, 1)).x*999);
  n += saturate(tex2D(sampler_pw_main, uv+d*float2( 1, 0)).x*999);
  n += saturate(tex2D(sampler_pw_main, uv+d*float2( 1,-1)).x*999);
  n += saturate(tex2D(sampler_pw_main, uv+d*float2( 0, 1)).x*999);
  n += saturate(tex2D(sampler_pw_main, uv+d*float2( 0,-1)).x*999);
  n += saturate(tex2D(sampler_pw_main, uv+d*float2(-1, 1)).x*999);
  n += saturate(tex2D(sampler_pw_main, uv+d*float2(-1, 0)).x*999);
  n += saturate(tex2D(sampler_pw_main, uv+d*float2(-1,-1)).x*999);

  if (n < 1.5)
    ret = 0;
  if (n > 3.5)
    ret = 0;
  if (ret.x < 0.1 && abs(n-3) < 0.5)
    ret = 1;

  // darken (decay) over time
 // ret = (ret - 0.016)*0.985;
}

comp_hlsl:
shader_body
{
    // use 3D noise 
    float3 uvw = float3(uv*8, 0.3);
    //uvw = mul(uvw, rot_s2);
    uvw.z += time*0.3;

    // sample a high-quality 3D noise volume!
    float4 N = tex3D(sampler_noisevol_hq, uvw);

    float2 uv2 = uv;
    uv2 += (N.xy*2-1)*texsize.zw*3;

    ret = tex2D(sampler_main, uv2).xyz;
    ret += GetBlur1(uv)*0.4;

    // palettize:
    ret = pow(ret, float3(0.5, 0.8, 1.7));

    //ret.xyz *= 2; // a little bit of overbright
}
