comp:
shader_body { 
 vec3 ret_1;
 ret_1 = (texture (sampler_main, uv).xyz * 0.8);
 ret_1 = (ret_1 + ((
   clamp ((((texture (sampler_blur1, uv).xyz * scale1) + bias1) - blur1_min), 0.0, 1.0)
  * 14.0) + (
   clamp ((((texture (sampler_blur2, uv).xyz * scale2) + bias2) - blur2_min), 0.0, 1.0)
  * 14.0)));
 ret_1 = (ret_1 * 0.7);
 ret_1 = (mix (vec3(dot (ret_1, vec3(0.32, 0.49, 0.29))), ret_1, vec3(0.2, 0.2, 0.2)) * ((hue_shader * 2.4) - 1.0));
 vec4 tmpvar_2;
 tmpvar_2.w = 1.0;
 tmpvar_2.xyz = ret_1;
 ret = tmpvar_2.xyz;
}

comp_hlsl:
shader_body
{
    ret = tex2D(sampler_main, uv).xyz*0.8;

    float3 blur = saturate(GetBlur1(uv) - blur1_min)*14
                + saturate(GetBlur2(uv) - blur2_min)*14;
    ret += blur;
    ret *= 0.7;
    ret = lerp(lum(ret), ret, 0.2);
    ret *= hue_shader*2.4-1;
}
