warp_hlsl:
shader_body
{

    // sample previous frame 3 times, creating radial blur
    float2 v = normalize(uv - 0.5)*aspect.xy;
    //v = v.yx * float2(1,-1);
    v *= texsize.zw*3;
    ret = 0.25*(   tex2D( sampler_main, uv ).xyz
                   +  tex2D(sampler_main, uv + v*2.5 ).xyz
                   +  tex2D(sampler_main, uv + v*5.5 ).xyz
                   +  tex2D(sampler_main, uv + v*-4  ).xyz
    );

   // sample previous frame, with some trails...
    float trail_decay = 0.25*GetBlur1(uv);
    ret -= tex2D( sampler_fc_main, uv ).xyz*trail_decay;


    // darken over time
//    ret -= .006;
}

