warp_hlsl:
shader_body
{
    // sample previous frame 3 times, creating radial blur
    float2 v = normalize(uv - 0.5);
    //v = v.yx * float2(1,-1);
    v *= texsize.zw;
    ret = 0.2*(   tex2D( sampler_main, uv ).xyz
                   +  tex2D(sampler_main, uv - v*2.5 ).xyz
                   +  tex2D(sampler_main, uv - v*5.5 ).xyz
                   +  tex2D(sampler_main, uv - v*9  ).xyz
                   +  tex2D(sampler_main, uv - v*13 ).xyz
    );

    // darken over time
    ret *= 0.98; //or try: ret -= 0.004;
}

comp_hlsl:
shader_body
{
    ret = tex2D(sampler_main, uv).xyz;    

    float3 d = float3(texsize.zw, 0)*1.0;
    float b1 = lum(GetPixel(uv + d.xz));
    float b2 = lum(GetPixel(uv - d.xz));
    float b3 = lum(GetPixel(uv + d.zy));
    float b4 = lum(GetPixel(uv - d.zy));

    //ret = 0;
    ret *= -1; 
    ret += saturate((b1-b2)*64)*1.00*float3(1,0.6,0.1);
    ret += saturate((b3-b4)*64)*1.00*float3(0.2,0.5,0.7);
    ret -= saturate( GetBlur1(uv)*4 - 1 );

    ret *= 1.5;
}
