}

comp:
shader_body { 
 vec2 uv_1;
 vec2 uv3_2;
 vec2 uv2_3;
 vec3 ret_4;
 uv_1 = (uv - 0.5);
 uv_1 = (uv_1 * aspect.xy);
 uv2_3.x = ((uv_1.x * 0.0007962743) - (uv_1.y * 0.9999997));
 uv2_3.y = ((uv_1.x * 0.9999997) + (uv_1.y * 0.0007962743));
 uv3_2.x = ((uv_1.x * -0.00238894) - (uv_1.y * -0.9999971));
 uv3_2.y = ((uv_1.x * -0.9999971) + (uv_1.y * -0.00238894));
 ret_4 = (max (max (
   max (texture (sampler_main, (uv_1 + 0.5)).xyz, texture (sampler_main, (uv2_3 + 0.5)).zxy)
 , texture (sampler_main, 
   (uv3_2 + 0.5)
 ).yzx), texture (sampler_main, (
   (1.0 - uv_1)
  + 0.5)).zyx) * 1.3);
 ret_4 = (ret_4 * pow (hue_shader, vec3(4.0, 4.0, 4.0)));
 ret_4 = (ret_4 + ((
   ((((texture (sampler_blur1, 
     (uv_1 + 0.5)
   ).xyz * scale1) + bias1) + ((texture (sampler_blur1, 
