}

warp_hlsl:
shader_body
{
    // sample previous frame
    ret = tex2D( sampler_main, uv ).xyz;
    ret=max(ret,tex2D(sampler_main,lerp(uv,uv_orig,0.3))*0.98);

    // darken (decay) over time
    ret *= 0.97; //or try: ret -= 0.004;
}

comp_hlsl:
shader_body
{
    //rotation code by geiss, mods by me
    float2 uv2, uv3;
    float ang2, c, s;

    uv -= 0.5;
    uv *= aspect.xy;    

    ang2 = 1.57;
    c = cos(ang2);
    s = sin(ang2);
    uv2.x =  uv.x*c - uv.y*s;
    uv2.y =  uv.x*s + uv.y*c;

    ang2 = 4.71;
    c = cos(ang2);
    s = sin(ang2);
    uv3.x =  uv.x*c - uv.y*s;
    uv3.y =  uv.x*s + uv.y*c;

    ret = tex2D(sampler_main, uv + 0.5).xyz;
    ret = max(ret, tex2D(sampler_main,uv2 + 0.5).zxy);
    ret = max(ret, tex2D(sampler_main,uv3 + 0.5).yzx);
    ret = max(ret, tex2D(sampler_main,(1-uv)+0.5).zyx);

    ret *= 1.3;
    ret*=pow(hue_shader,4);
    ret+=(GetBlur1(uv+0.5)+GetBlur1(uv2+0.5).zxy+GetBlur1(uv3+0.5).yzx+GetBlur1(uv*float2(-1,-1)+0.5).zyx)/4;
    ret*=dot(ret,hue_shader.zxy);
}
