frame_eqs_eel:
warp=0;
le=1+.5+2*sin(bass_att);
bpulse=band(above(le,bth),above(le-bth,bblock));
bblock=le-bth;
bth=if(above(le,bth),le+114/(le+10)-7.407,
bth+bth*.07/(bth-12)+below(bth,2.7)*.1*(2.7-bth));
bth=if(above(bth,6),6,bth);
bres=bpulse*sin(pulse+le*.5) + bnot(bpulse)*bres;

le=1+.5+2*sin(treb_att);
tpulse=band(above(le,tth),above(le-tth,tblock));
tblock=le-tth;
tth=if(above(le,tth),le+114/(le+10)-7.407,
tth+tth*.07/(tth-12)+below(tth,2.7)*.1*(2.7-tth));
tth=if(above(tth,6),6,tth);
tres=tpulse*sin(pulse+le*.5) + bnot(tpulse)*tres;

le=1+.5+2*sin(mid_att);
mpulse=band(above(le,mth),above(le-mth,mblock));
mblock=le-mth;
mth=if(above(le,mth),le+114/(le+10)-7.407,
mth+mth*.07/(mth-12)+below(mth,2.7)*.1*(2.7-mth));
mth=if(above(mth,6),6,mth);
mres=mpulse*sin(pulse+le*.5) + bnot(mpulse)*mres;



pulse=if(above(abs(pulse),3.14),-3.14,pulse+(bth+mth+tth)*.003);
q1=bres;
q2=tres;
q3=mres;
q4=sin(pulse);

mod_state=(above(q1,0)+above(q2,0)+above(q3,0))*(1+above(q4,0));
ccl=ccl+tpulse+mpulse-bpulse;
q5=cos(pulse*(.5+.1*mod_state));
q6=sin(pulse*(.5+pow(.25,mod_state)));
q7=mod_state;
q8=ccl;
ib_size=.025+.02*q2;
ob_size=.03+.02*q3-.002*q7;
wave_mystery=wave_mystery+sin(time*2.18+q6);
wave_x=wave_x+.15*sin(time*.811+q1)+.02*(frame%12+q7)*sign(q5);
wave_y=wave_y+.15*sin(time*.788+q2)+.02*(frame%11+q7)*sign(q3);

cy=.5+.5*q4+sin(time*.086);

decay=.997+.0015*q3+.0015*q1;


slowtime = slowtime + mpulse*time;


temp_b = cos(time)  + abs(cos(time));
temp_g = abs(sin(time)) ;
temp_r = (-1 * cos(time))  + abs(-1 * cos(time)) + 0.2 * (cos(sin(time))+(abs(cos(sin(time)))+cos(sin(time))));
//ob_r = if(above(temp_r,1),1,if(above(temp_r,0), abs(temp_r),0));
//ob_g = if(above(temp_g,1),1,if(above(temp_g,0), abs(temp_g),0));
//ob_b = if(above(temp_b,1),1,if(above(temp_b,0), abs(temp_b),0));
//ib_r =  1- if(above(temp_r,1),1,if(above(temp_r,0), abs(temp_r),0));
//ib_g =  1- if(above(temp_g,1),1,if(above(temp_g,0), abs(temp_g),0));
//ib_b =  1- if(above(temp_b,1),1,if(above(temp_b,0), abs(temp_b),0));
wave_b = 1-temp_b;
wave_g = 1-temp_g;
wave_r = 1-temp_r;
monitor = equal(ob_b,1);
monitor = wave_b;

warp:
shader_body { 
 vec2 uv_1;
 vec2 tmpvar_2;
 tmpvar_2 = normalize((uv_orig - 0.5));
 float tmpvar_3;
 tmpvar_3 = (time * 7.0);
 uv_1 = (mix (uv_orig, uv, vec2((
   clamp ((((bass / bass_att) - 0.9) * 13.0), 0.0, 1.0)
  * 0.3))) + ((
   (tmpvar_2 * texsize.zw)
  * 
   cos(((rad * 170.0) - tmpvar_3))
 ) * 2.0));
 uv_1 = (uv_1 + ((
   (vec2(5.0, -5.0) * texsize.zw)
  * 
   cos(((ang * 30.0) - tmpvar_3))
 ) * tmpvar_2.yx));
 vec4 tmpvar_4;
 tmpvar_4.w = 1.0;
 tmpvar_4.xyz = ((texture (sampler_main, uv_1).xyz - 0.004) * 0.83);
 ret = tmpvar_4.xyz;
}

warp_hlsl:
shader_body
{
    float t = saturate((bass/bass_att-0.9)*13);
    uv = lerp(uv_orig, uv, t*0.3);

    float2 v1 = normalize(uv_orig-0.5);
    float2 v2 = v1.yx * float2(1,-1);

    float z = length(texsize.zw)*450;

    uv.xy += v1*texsize.zw * cos(rad*170 - time*7)*2;
    uv.xy += v2*texsize.zw * cos(ang*30 - time*7)*5;

    // sample previous frame
    ret = tex2D( sampler_main, uv ).xyz;



    // darken (decay) over time
    ret = (ret - 0.004)*0.83;
}
