frame_eqs_eel:
warp=0;
old_bass_flop=bass_flop;
old_treb_flop=treb_flop;
old_mid_flop=mid_flop;
chaos=bnot(pulse)*(.8+.2*sin(pulse));
entropy=if(chaos,int(rand(10)),entropy);
bass_thresh = above(bass_att,bass_thresh)*2 + (1-above(bass_att,bass_thresh))*((bass_thresh-1.3)*chaos+1.3);
bass_flop=abs(bass_flop-equal(bass_thresh,2));
treb_thresh=above(treb_att,treb_thresh)*2 + (1-above(treb_att,treb_thresh))*((treb_thresh-1.3)*chaos+1.3);
treb_flop=abs(treb_flop-equal(treb_thresh,2));
mid_thresh=above(mid_att,mid_thresh)*2 + (1-above(mid_att,mid_thresh))*((mid_thresh-1.3)*chaos+1.3);
mid_flop=abs(mid_flop-equal(mid_thresh,2));
bass_changed=bnot(equal(old_bass_flop,bass_flop));
mid_changed=bnot(equal(old_mid_flop,mid_flop));
treb_changed=bnot(equal(old_treb_flop,treb_flop));
pulse=if(above(abs(pulse),5000),-5000,pulse+mid_att*bor(bor(bass_changed*bnot(treb_changed),treb_changed*bnot(bass_changed))*bnot(mid_changed),mid_changed));
pulse_timer=sin(pulse);
effect_1=1+1*bass_flop*above(pulse_timer,0);
effect_2=1+2*treb_flop*above(pulse_timer,0);
effect_3=1+4*bass_flop*below(pulse_timer,0);
effect_4=1+6*treb_flop*below(pulse_timer,0);
effect_5=1+10*bass_flop*mid_flop*above(pulse_timer,.5);
effect_6=1+12*treb_flop*mid*below(pulse_timer,-.5);
q1=effect_1*effect_2*effect_3*effect_4*effect_5*effect_6;
q2=time*q1*.3*entropy;
q3=pulse_timer;
q4=sin(q2*.002);
wave_r=if(mid_flop,bass_flop,.5*q3*q4);
wave_b=if(treb_flop,mid_flop,.5*q3);
wave_g=if(bass_flop,treb_flop,+.5*q4);
zoom_fade=if(above(q3,0),if(above(q4,0),zoom_fade-.005*mid_att,zoom_fade+.005*mid_att),1);
zoom=zoom_fade;
ob_r=if(mid_flop,wave_g,wave_b);
ob_b=if(treb_flop,wave_r,wave_g);
ob_g=if(bass_flop,wave_b,wave_r);
ob_a=.09+.05*q3;
ob_size=.01+.009*q4;
decay=.982+.01*q3*q4;
q20=bass+treb+mid;

pixel_eqs_eel:
g1=sin(q2+time*.007);
g2=sin(q2+time*.005);
g3=sin(q2+time*.003);
x_shift=pow(x,4*abs(g2))+x*g1+sqr(g1);
y_shift=pow(y,4*abs(g3))+y*g2+sqr(g2);
r_shift=pow(rad,4*abs(g1))+rad*g3+sqr(g3);
dx=sin(x_shift*y_shift*r_shift)*g1*.1;
dy=if(above(q4,0),sin(x_shift*y_shift*r_shift)*g3*.1,sin(x_shift*y_shift*r_shift)*g2*.1);
rot=if(above(sin(q2*.003),0),0,rot+min(q3,dx)*g3);

